function drawChart(){var a=new google.visualization.arrayToDataTable([["Mois","Nombres de visites"],["Janvier",50],["Février",55],["Mars",45],["Avril",60],["Mai",40],["Juin",65],["Juillet",80],["Août",74],["Septembre",66]]);new google.visualization.ColumnChart(document.getElementById("chart_div1")).draw(a,{title:"Nombres de visites par mois"});var e=new google.visualization.arrayToDataTable([["Navigateur","Nombres d'utilisateurs"],["IE",3],["Mozilla",20],["Chrome",50],["Safari",30],["Opera",10]]);new google.visualization.PieChart(document.getElementById("chart_div2")).draw(e,{title:"Navigateurs utilisés"})}google.charts.load("current",{packages:["corechart"]}),google.charts.setOnLoadCallback(drawChart),$(window).resize(function(){drawChart()});